import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FaBars, FaTimes, FaUserCircle, FaHome } from 'react-icons/fa';
import logo from '../assets/logo.jpg';
import './Navbar.css';

const Navbar = () => {
    const [isOpen, setIsOpen] = useState(false);

    const toggleMenu = () => setIsOpen(!isOpen);

    const navLinks = [
        { name: 'About', path: '/about' },
        { name: 'Products', path: '/products' },
        { name: 'Buyer', path: '/buyer' },
        { name: 'Sellers', path: '/sellers' },
        { name: 'Faq', path: '/faq' },
        { name: 'Contact', path: '/contact' },
        { name: 'Blogs', path: '/blogs' },
        { name: 'Resources', path: '/resources' },
        { name: 'Tracking', path: '/tracking' },
    ];

    return (
        <nav className="navbar">
            <div className="navbar-container">
                <Link to="/" className="navbar-logo">
                    <img src={logo} alt="World of Steel" />
                </Link>

                <div className="menu-icon" onClick={toggleMenu}>
                    {isOpen ? <FaTimes /> : <FaBars />}
                </div>

                <ul className={isOpen ? 'nav-menu active' : 'nav-menu'}>
                    <li className="nav-item">
                        <Link to="/" className="nav-links" onClick={toggleMenu}>
                            <FaHome size={24} />
                        </Link>
                    </li>
                    {navLinks.map((link, index) => (
                        <li key={index} className="nav-item">
                            <Link to={link.path} className="nav-links" onClick={toggleMenu}>
                                {link.name}
                            </Link>
                        </li>
                    ))}
                    <li className="nav-item mobile-only">
                        <Link to="/login" className="nav-links-btn" onClick={toggleMenu}>
                            Login / Sign Up
                        </Link>
                    </li>
                </ul>

                <div className="nav-btn">
                    <Link to="/login" className="btn-primary">
                        <FaUserCircle className="btn-icon" /> Login / Sign Up
                    </Link>
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
